import React, { useState } from 'react';
import './assets/css/style.css';
import 'bootstrap/dist/css/bootstrap.min.css'
import { useSelector } from 'react-redux';
import MovieOne from './components/MovieOne';
// import MovieTwo from './components/MovieTwo';
// import MovieTre from './components/MovieTre';

function App() {

  const [todos, setTodos] = useState([
    { "id": 1, "title": "Delectus aut autem" },
    { "id": 2, "title": "Suis ut nam facilis et officia qui" },
    { "id": 3, "title": "Fugiat veniam minus" },
    { "id": 4, "title": "Dt porro tempora" },
    { "id": 5, "title": "Laboriosam mollitia et enim quasi adipisci quia provident illum" },
    { "id": 6, "title": "Qui ullam ratione quibusdam voluptatem quia omnis" },
    { "id": 7, "title": "Mllo expedita consequatur quia in" },
    { "id": 8, "title": "Quo adipisci enim quam ut ab" },
    { "id": 9, "title": "Molestiae perspiciatis ipsa" },
    { "id": 10, "title": "Lollo est ratione doloremque quia maiores aut" }
  ])
  const [search, setSearch] = useState('');
  const state = useSelector(state => state)

  const [movieone] = useState(state.movie_one.initialState);
  // const [movietwo] = useState(state.movie_two.initialState);
  // const [movietre] = useState(state.movie_three.initialState)

  const searchTodos = (e) => {
    setSearch(e.target.value)
    console.log(search)
  }

  return (
    <div className="">

      <input type="text" value={search} onChange={searchTodos} placeholder="Search..." />

      <ul>
      {todos.map(items=>{
        return(<li key={items.id}>{items.title}</li>)
      })}

      </ul>

      {
        // <MovieOne data={movieone} />
        // <MovieTwo data={movie_two} />
        // <MovieTre data={movie_tre} />
      }
    </div>
  );
}

export default App;
