import React, { Component } from 'react';
import './assets/css/style.css';
import 'bootstrap/dist/css/bootstrap.min.css'
import { todos } from './todos';
class App extends Component {
  state = {
    todos: todos,
    search: '',
    noresult: ''
  }
  searchItems(e) {
    let result = e.target.value.toLocaleLowerCase();
    this.setState({ search: e.target.value })
    if (result !== '') {
      this.setState({ todos: todos.filter(items => items.name.toLocaleLowerCase().indexOf(result) !== -1) })
      if (this.state.todos.length == 0) {
        this.setState({ noresult: 'No Result' })
      } else {
        this.setState({ noresult: '' })
      }
    } else {
      this.setState({ todos: todos })
    }
  }



  render() {
    const { todos } = this.state;
    const { noresult } = this.state
    const todosList = todos.map(items => {
      return <li className="list-group-item py-1" key={items.id}>{items.name}</li>
    })
    return (
      <div className="container">
        <ul className="list-group">
          <li className="list-group-item">
            <input typ="text"
              className="form-control"
              name="search"
              value={this.state.search}
              onChange={this.searchItems.bind(this)}
            />
          </li>

          {todosList}

          <React.Fragment>{noresult ? <li className="list-group-item">{noresult}</li> : ''}</React.Fragment>
        </ul>
      </div>
    );
  }
}

export default App;
