import React, { Component } from 'react';
import './assets/css/style.css';
import 'bootstrap/dist/css/bootstrap.min.css'
import { todos } from './todos';
class App extends Component {
  state = {
    todos: todos,
    search: '',
  }
  searchItems(e) {
    let result = e.target.value.toLocaleLowerCase();
    this.setState({ search: e.target.value })
    if (result !== '') {
      this.setState({ todos: todos.filter(items => items.name.toLocaleLowerCase().indexOf(result) !== -1) })
    } else {
      this.setState({ todos: todos })
    }
  }



  render() {
    const { todos } = this.state;
    const todosList = todos.map(items => {
      return (
        <div className="input-group" key={items.id}>
          <div className="input-group-prepend">
            <span className="input-group-text">{items.id}</span>
          </div>



          <div className="form-control">

            {items.name}
          </div>
          <div className="input-group-append">
            <span className="input-group-text">{items.id}</span>
          </div>

        </div>
      )

    })
    return (
      <div className="container">
        <div>
          <li className="list-group-item">
            <input typ="text"
              className="form-control"
              name="search"
              value={this.state.search}
              onChange={this.searchItems.bind(this)}
            />
          </li>
          {todosList}
        </div>
      </div>
    );
  }
}

export default App;
