import React, { useState } from 'react';
import './assets/css/style.css';
import 'bootstrap/dist/css/bootstrap.min.css'
import { useSelector } from 'react-redux';
import MovieOne from './components/MovieOne';
// import MovieTwo from './components/MovieTwo';
// import MovieTre from './components/MovieTre';

function App() {
  const state = useSelector(state => state)
  const [search, setSearch] = useState("")
  const [movieone, setMovieone] = useState(state.movie_one.initialState);


  // const [movietwo, setMovietwo] = useState(state.movie_two.initialState);
  // const [movietre, setMovietre] = useState(state.movie_three.initialState)

  const contentone = movieone.list[0].page['content-items']['content'];

  const searchMovie = (e) => {
    setSearch(e.target.value)
    contentone.filter(items => items.name.indexOf(search) != -1)
  }

  return (
    <div className="">
      <MovieOne data={movieone} searchMovie={searchMovie} search={search} />
      {
        // <MovieTwo data={movie_two} />
        // <MovieTre data={movie_tre} />
      }
    </div>
  );
}

export default App;
