import React, { Component } from 'react';
import './assets/css/style.css';
import 'bootstrap/dist/css/bootstrap.min.css'
import { todos } from './todos';
class App extends Component {
  state = {
    todos: todos,
    search: ''
  }
  searchItems(e) {
    let result = e.target.value.toLocaleLowerCase();
    this.setState({ search: e.target.value })
    if (result !== '') {
      this.setState({ todos: todos.filter(items => items.title.toLocaleLowerCase().indexOf(result) !== -1) })
    } else {
      this.setState({ todos: todos })
    }
  }



  render() {
    const { todos } = this.state;
    const todosList = todos.map(items => {
      return <li className="list-group-item py-1" key={items.id}>{items.title}</li>
    })
    return (
      <div className="container">
        <ul className="list-group">
          <li className="list-group-item">
            <input typ="text"
              className="form-control"
              name="search"
              value={this.state.search}
              onChange={this.searchItems.bind(this)}
            />
          </li>

          {todosList}
        </ul>
      </div>
    );
  }
}

export default App;
