import React, { useState, useEffect } from 'react'
import { Container, Card, Row, Col, InputGroup, Form } from 'react-bootstrap'
const MovieOne = ({ data }) => {
    const content = data.list[0].page["content-items"]["content"];
    const [movies, setMovies] = useState(content);
    const pagetitle = data.list[0].page.title
    const [searchTerm, setSearchTerm] = useState("");
    const [searchResults, setSearchResults] = useState([]);

    const handleChange = event => { setSearchTerm(event.target.value); };



    const checkMovies = () => {
        console.log(movies)
    }

    useEffect(() => {
        const results = movies.filter(items =>
          items.toLowerCase().includes(searchTerm)
        );
        setSearchResults(results);
      }, [searchTerm]);

    return (
        <React.Fragment>
            <Container>
                <h4 onClick={checkMovies} className="m-0 py-3">{pagetitle}</h4>
            </Container>
            <hr className="m-0 mb-3" />
            <Container>
                <InputGroup>
                    <Form.Control
                        type="text"
                        placeholder="Search Movie..."
                        aria-describedby="inputGroupPrepend"
                        required
                        name="search"
                        value={searchTerm}
                        onChange={handleChange}
                    />
                    <InputGroup.Append>
                        <InputGroup.Text id="inputGroupPrepend"><i className="fad fa-search"></i></InputGroup.Text>
                    </InputGroup.Append>
                </InputGroup>
                <hr />
                <Row>
                    {setSearchResults.map((items, index) => {
                        return (
                            <Col xs={6} sm={6} md={4} lg={3} className="mb-5" key={index}>
                                <Card>
                                    <Card.Img src={'/Slice/' + items['poster-image']} />
                                    <Card.Body>
                                        <Card.Title>{items.name}</Card.Title>
                                    </Card.Body>
                                </Card>
                            </Col>

                        )
                    })}
                </Row>
            </Container>

        </React.Fragment>
    )

}
console.warn = () => { }
export default MovieOne
