import React, { useState } from 'react'
import { Container, Card, Row, Col, InputGroup, Form } from 'react-bootstrap'
const MovieOne = ({ data }) => {
    const [movies, setMovies] = useState(data.list[0].page["content-items"]["content"]);
    const [search, setSearch] = useState('');
    const pagetitle = data.list[0].page.title


    const searchMovie = (e) => {
        setSearch(e.target.value.toLocaleLowerCase())
        if (search !== '') {
            setMovies(movies.filter(items => items.name.toLocaleLowerCase().indexOf(search) !== -1))
            //setState({ todos: todos.filter(items => items.name.toLocaleLowerCase().indexOf(result) !== -1) })
        } else {
            setMovies([...movies])
        }
    }


    return (
        <React.Fragment>
            <Container>
                <h4 className="m-0 py-3">{pagetitle}</h4>
            </Container>
            <hr className="m-0 mb-3" />
            <Container>
                <InputGroup>
                    <Form.Control
                        type="text"
                        placeholder="Search Movie..."
                        aria-describedby="inputGroupPrepend"
                        required
                        name="search"
                        value={search}
                        onChange={searchMovie}
                    />
                    <InputGroup.Append>
                        <InputGroup.Text id="inputGroupPrepend"><i className="fad fa-search"></i></InputGroup.Text>
                    </InputGroup.Append>
                </InputGroup>
                <hr />
                <Row>
                    {movies.map((items, index) => {
                        return (
                            <Col xs={6} sm={6} md={4} lg={3} className="mb-5" key={index}>
                                <Card>
                                    <Card.Img src={'/Slice/' + items['poster-image']} />
                                    <Card.Body>
                                        <Card.Title>{items.name}</Card.Title>
                                    </Card.Body>
                                </Card>
                            </Col>

                        )
                    })}
                </Row>
            </Container>

        </React.Fragment>
    )

}
console.warn = () => { }
export default MovieOne
